// src/components/Slider/index.tsx
import React, { useState, useEffect } from 'react';
// import Slider from 'react-slick';
import {
    SlideContainer,
    Slide,
    LeftCol,
    Caption,
    CaptionHeading,
    CaptionSubhead,
    Button
    
} from './styles';

const slideData = [
    {
      src: 'couch-area.jpg',
      title: 'Slide 1',
      copy: 'DOLOR SIT AMET, CONSECTETUR ADIPISCING ELIT.',
    },
    {
      src: 'couch-area.jpg',
      title: 'Slide 1',
      copy: 'DOLOR SIT AMET, CONSECTETUR ADIPISCING ELIT.',
    },
    {
      src: 'couch-area.jpg',
      title: 'Slide 1',
      copy: 'DOLOR SIT AMET, CONSECTETUR ADIPISCING ELIT.',
    },
];

interface SlideProps {
    isCurrentSlide: boolean;
    isNextSlide: boolean;
    isPreviousSlide: boolean;
    style: any;
}

const Slider = ({ slideData } : any) => {
    const [currentIndex, setCurrentIndex] = useState(0);
  
    const handleSlideChange = (index) => {
      setCurrentIndex(index);
    };
  
    return (
      <SlideContainer>
        <LeftCol>
          {slideData.map((slide, index) => (
            <Slide
              key={index}
              isCurrentSlide={index === currentIndex}
              isNextSlide={index === currentIndex + 1}
              isPreviousSlide={
                index === (slideData.length + currentIndex - 1) % slideData.length
              }
              style={{ backgroundImage: `url(${slide.imageUrl})` }}
            >
              <Caption
                isCurrentCaption={index === currentIndex}
                isPreviousCaption={
                  index === (slideData.length + currentIndex - 1) % slideData.length
                }
              >
                <CaptionHeading>{slide.heading}</CaptionHeading>
                <CaptionSubhead>{slide.subheading}</CaptionSubhead>
                <Button href={slide.buttonLink}>{slide.buttonText}</Button>
              </Caption>
            </Slide>
          ))}
        </LeftCol>
        {/* Add your right column and navigation components here */}
      </SlideContainer>
    );
  };
  
export default Slider;
