import styled from 'styled-components';

export const GalleryContainer = styled.div`
    padding-bottom: 10%;
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    margin: 0 -15px;
`;

export const Card = styled.div`
    border: 1px solid #333;
    width: 300px;
    margin: 15px;
    text-align: center;
    display: flex;
    align-items: center;
    justify-content: center;

    cursor: pointer;
    transition: 1s;

    &::before, &::after {
        position: absolute;
        background: #eee;
        z-index: -1;
        transition: 1s;
        content: '';
    }

    &::before {
        height: 50px;
        width: 130px;
    }

    &::after {
        height: 150px;
        height: 30px;
    }
`;

export const Image = styled.img`
    width: 100%;
    height: 200px; /* Fixed height for the images */
    object-fit: cover; /* Maintain aspect ratio and fill the container */
`;

export const Title = styled.h3`
    margin-top: 10px;
    font-size: 2rem;
`;
