import React from 'react';
import { ImageProps } from "../types";
import fs from 'fs';
import path from 'path';

export const Image = ({ src, width, height }: ImageProps) => {
  const publicDir = path.join(__dirname, '../../public');
  const imgPath = path.join(publicDir, `images/${src}`);
  const svgPath = path.join(publicDir, `svg/${src}`);

  let imageSrc = '';

  try {
    // check for image in the image folder
    const imageFiles = fs.readdirSync(imgPath);

    if (imageFiles.includes(src)){
      imageSrc = `/img/${src}`;
    }
  } catch (err){
    console.error('Error checking "img" folder for image: ', err);
  }

  try {
    // check for image in the svg folder
    const svgFiles = fs.readdirSync(svgPath);

    if (svgFiles.includes(src)){
      imageSrc = `/svg/${src}`;
    }
  } catch (err){
    console.error('Error checking "svg" folder: ', err)
  }

  if (imageSrc) {
    return <img src={imageSrc} alt={src} width={width} height={height} />;
  } else {
    return <div>Image not found</div>;
  }
};
