import styled from 'styled-components';
import { StyledContainer } from '../../common/Container/styles';

export const SliderContainer = styled(StyledContainer)`
    padding: 5% 0;
`;

export const SliderWrapper = styled.div`
  position: relative;
  height: 100vh;
  width: 100%;
  background-color: #2c302e;
  display: flex;
`;

export const SlideContent = styled.div<{ active: boolean }>`
  position: absolute;
  top: 50%;
  left: 8rem;
  z-index: 9;
  transform: translateY(-50%);
  opacity: ${(props) => (props.active ? 1 : 0)};
  transition: 500ms ease opacity, 500ms ease transform;
  transform: ${(props) => (props.active ? 'translateY(0)' : 'translateY(60px)')};

  h1 {
    font-size: 4rem;
    margin-bottom: 1.5rem;
    text-transform: uppercase;
    text-indent: -0.2rem;
    letter-spacing: 0.2rem;
    font-weight: 300;
  }

  span {
    font-size: 1rem;
    font-weight: 300;
    text-transform: uppercase;
    color: white;
    letter-spacing: 4px;
    word-spacing: 0.1rem;
    margin-bottom: 2.5rem;
    display: block;
  }

  a {
    color: #333;
    font-size: 0.8rem;
    text-decoration: none;
    background-color: white;
    padding: 0.5rem 1rem;
    text-transform: uppercase;
    letter-spacing: 0.2rem;
    position: relative;
    z-index: 9;
    transition: 250ms ease-in background-color, 500ms ease-in color;

    &:hover {
      background-color: black;
      color: white;
    }
  }
`;

export const SlideImage = styled.div`
  width: 100%;
  height: calc(100% - 4rem);
  overflow: hidden;
  margin: 2rem;
  position: relative;
  background-size: cover;
  background-position: center top;
  filter: grayscale(100%);
`;
