import styled from 'styled-components';
import { StyledContainer } from '../../common/Container/styles';

export const SliderContainer = styled(StyledContainer)`
    padding: 5% 0;
`;

export const SliderWrapper = styled.div`
  position: relative;
  height: 80vh;
  width: 100%;
  background-color: #2c302e;
  display: flex;
`;

export const SlideContent = styled.div<{ active: boolean }>`
  position: absolute;
  top: 50%;
  left: 8rem;
  z-index: 9;
  transform: translateY(-250%);
  opacity: ${(props) => (props.active ? 1 : 0)};
  transition: 500ms ease opacity, 500ms ease transform;
  transform: ${(props) => (props.active ? 'translateY(0)' : 'translateY(60px)')};
`;

export const SlideImage = styled.div`
  width: 100%;
  height: calc(100% - 4rem);
  overflow: hidden;
  margin: 2rem;
  position: relative;
  background-size: cover;
  background-position: center top;
  filter: grayscale(100%);
`;
