// src/components/Slider/index.tsx
import React from 'react';
import Slider from 'react-slick';
import {
    SliderContainer,
    SliderWrapper,
    SlideImage,
    SlideContent
} from './styles';

const slideData = [
  {
    src: 'couch-area.jpg',
    title: 'Slide 1',
    copy: 'DOLOR SIT AMET, CONSECTETUR ADIPISCING ELIT.',
  },
];

const SliderComponent = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 5000,
    pauseOnHover: true,
  };

  return (
    <SliderContainer>
      <SliderWrapper>
        <Slider {...settings}>
          {slideData.map((slide, index) => (
            <div key={index}>
              <SlideImage style={{ backgroundImage: `url(${slide.src})` }} />
              <SlideContent active={index === 0}>
                <h1>{slide.title}</h1>
                <span>{slide.copy}</span>
                <a href="#">Sit Amet</a>
              </SlideContent>
            </div>
          ))}
        </Slider>
      </SliderWrapper>
    </SliderContainer>
  );
};

export default SliderComponent;
