import styled, { css, keyframes } from'styled-components';

export const ScrollDownContainer = styled.div<{ isScrolling: boolean }>`
  position: absolute;
  bottom: 50px;
  color: #ccc;
  left: 50%;
  font-weight: lightest;
  transform: translateX(-50%);
  cursor: pointer;
  animation: ${({ isScrolling }) =>
    isScrolling
      ? css`
          ${ScrollUpAnimation} 0.5s ease-in-out infinite alternate;
        `
      : 'none'};
`;

export const ScrollUpAnimation = keyframes`
  0% {
    transform: translateY(0);
  }
  100% {
    transform: translateY(-10px);
  }
`;

export const ScrollDownArrow = styled.div`
  width: 0;
  height: 0;
  border-left: 10px solid transparent;
  border-right: 10px solid transparent;
  border-top: 10px solid #333;
`;

export const Span = styled.span`
  font-size: 1.5rem;
`;
